 Router 

```java
public class CompleteRouterDemo {
    public static void main(String[] args) {
        Router router = new Router();

        //  Session
        router.getSessionOptions().setMaxAge(1800); // 30

        // 
        router.addInterceptor("/*", (ctx, future, chain) -> {
            long start = System.currentTimeMillis();
            System.out.println(": " + ctx.Request.getRequestURI());
            try {
                chain.proceed(ctx, future);
            } finally {
                long end = System.currentTimeMillis();
                System.out.println(": " + (end - start) + "ms");
            }
        });

        // 
        router.route("/", ctx -> {
            ctx.Response.write(" Feat Router");
        });

        // Session 
        router.route("/login", ctx -> {
            Session session = ctx.session();
            session.put("loginTime", System.currentTimeMillis());
            session.put("username", "");
            ctx.Response.write("");
        });

        router.route("/user/:id", ctx -> {
            String id = ctx.pathParam("id");
            Session session = ctx.session(); //  Session 
            if (session != null) {
                Long loginTime = (Long) session.get("loginTime");
                ctx.Response.write(" " + id + ": " + loginTime);
            } else {
                ctx.Response.setHttpStatus(HttpStatus.UNAUTHORIZED);
                ctx.Response.write("");
            }
        });

        // 
        router.route("/api/*", ctx -> {
            ctx.Response.write("API : " + ctx.Request.getRequestURI());
        });

        // 
        Feat.httpServer()
            .httpHandler(router)
            .listen(8080);
    }
}
